import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Actions } from '@ngrx/effects';
import { Store, select } from '@ngrx/store';
import { Observable } from 'rxjs';
import { Todo } from 'src/app/models/Todo';
import { AppState } from 'src/app/state/app.state';
import { addTodo, handleCompleted, loadTodos, removeTodo } from 'src/app/state/todos/todo.actions';
import { isLoading, selectAllTodos } from 'src/app/state/todos/todo.selectors';
@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css']
})
export class TodosComponent implements OnInit {
  // public allTodos$ = this.store.select(selectAllTodos);
  nameControl!: FormControl
  importanceControl!: FormControl
  hoursControl!: FormControl
  minutesControl!: FormControl

  isLoading: Observable<boolean>

  todos: Observable<Todo[]>;

  addForm: FormGroup
  editForm!: FormGroup

  editIndex: number = -1;

  addFormOpen: boolean = false
  editFormOpen: boolean = false

  constructor(private store: Store<AppState>) {
    this.isLoading = this.store.pipe(select(isLoading))

    this.todos = this.store.pipe(select(selectAllTodos))

    this.addForm = new FormGroup({
      nameControl: new FormControl(''),
      importanceControl: new FormControl(''),
      hoursControl: new FormControl("12"),
      minutesControl: new FormControl("00")
    })
    this.editForm = new FormGroup({
      nameControl: new FormControl(''),
      importanceControl: new FormControl(''),
      hoursControl: new FormControl("12"),
      minutesControl: new FormControl("00")})
  }
  createFormControls() {
    this.nameControl = new FormControl('', Validators.required);
    this.importanceControl = new FormControl('', Validators.required);
    this.hoursControl = new FormControl('', Validators.required);
    this.minutesControl = new FormControl('', Validators.required);
  }

  createForm() {
    this.addForm = this.createFormFroup()
    this.editForm = this.createFormFroup()
  }

  createFormFroup() {
    return new FormGroup({
      deadline: new FormGroup({
        hoursControl: this.hoursControl,
        minutesControl: this.minutesControl,
      }),
      nameControl: this.nameControl,
      importanceControl: this.importanceControl,
      // language: this.language
    });
  }

  ngOnInit(): void { 
    this.store.dispatch(loadTodos());
  }

  toggleDone (id : number) : void {
    this.store.dispatch(handleCompleted({id : id}))
    //this.todos.map((element, i) => { if(i == id) element.completed = !element.completed })
  }

  toggleEdit  (id : number, event : any) : void {
    event.stopPropagation()
    this.editFormOpen = true
    this.editIndex = id
    // console.log(this.todos.find(item => item.id == this.editIndex)?.content)
    // this.editForm.get('nameControl')?.setValue(this.todos.find(item => item.id === this.editIndex)?.content)
    // this.editForm.get('importanceControl')?.setValue(this.todos.find(item => item.id === this.editIndex)?.importance)
    // this.editForm.get('hoursControl')?.setValue(this.todos.find(item => item.id === this.editIndex)?.deadline.split(':')[0])
    // this.editForm.get('minutesControl')?.setValue(this.todos.find(item => item.id === this.editIndex)?.deadline.split(':')[1])
    // console.log(this.editForm.getRawValue())
  }

  toggleAddFormOpen () : void {
    this.addFormOpen = !this.addFormOpen
  }

  toggleEditFormOpen () : void {
    this.editFormOpen = !this.editFormOpen
  }

  deleteTodo(id : number) {
    this.store.dispatch(removeTodo({ id: id }));

  }

  addTodo () {
    if(this.addForm.get('nameControl')?.toString().trim() == "") return

    // var newId : number = 0
    // if(this.todos.length > 0) {
    //   newId = this.todos[this.todos.length - 1].id + 1
    // }

    this.store.dispatch(addTodo({ addForm: this.addForm}))
    this.toggleAddFormOpen()
    // this.addForm.setValue({
    //   nameControl: new FormControl(''),
    //   importanceControl: new FormControl(''),
    //   hoursControl: new FormControl("12"),
    //   minutesControl: new FormControl("00")
    // })

    this.addForm.get('nameControl')?.setValue(new FormControl(''))
    this.addForm.get('importanceControl')?.setValue(new FormControl(''))
    this.addForm.get('hoursControl')?.setValue(new FormControl('12'))
    this.addForm.get('minutesControl')?.setValue(new FormControl('00'))


  }

  editTodo() {    
    // const item = this.todos.find(item => item.id === this.editIndex)

    // if(item) {
    //   item.content = this.editForm.get('nameControl')!.value.toString()
    //   item.importance = this.editForm.get('importanceControl')!.value.toString()
    //   item.deadline = this.editForm.get('hoursControl')!.value.toString() + ":" + this.editForm.get('minutesControl')!.value.toString()
    // }

    this.toggleEditFormOpen()
  }
}
