import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Todo } from 'src/app/models/Todo';
@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css']
})
export class TodosComponent implements OnInit {

  todos!: Todo[];

  addForm: FormGroup

  inputTodo:string = ""
  newInputTodo:string = ""

  editIndex: number = 1;

  addFormOpen: boolean = true

  constructor() {
    this.addForm = new FormGroup({
      nameControl: new FormControl(),
      importanceControl: new FormControl(),
      hoursControl: new FormControl(),
      minutesControl: new FormControl()
    })
  }

  ngOnInit(): void {
    this.todos = [
      {
        id: 0,
        content: 'First todo',
        completed: false
      },
      {
        id: 1,
        content: 'Second todo',
        completed: true
      },
    ]
  }

  toggleDone (id : number) : void {
    console.log("done")
    this.todos.map((element, i) => {
      if(i == id) element.completed = !element.completed

      return element
    })
    this.editIndex = -1

    console.log(this.todos)
  }

  toggleEdit  (id : number, event : any) : void {
    event.stopPropagation()
    if(this.editIndex == id) {
      this.editIndex = -1
    } else {
      this.editIndex = id
    }
  }

  toggleAddFormOpen () : void {
    this.addFormOpen = !this.addFormOpen
  }

  deleteTodo(id : number) {
    this.todos = this.todos.filter((element, i) => i !== id)
    this.editIndex = -1
  }

  addTodo () {

    console.log(this.addForm)
    if(this.inputTodo.trim() == "") return

    var newId : number = 0
    if(this.todos.length > 0) {
      newId = this.todos[this.todos.length - 1].id + 1
    }

    this.todos.push({
      id: newId,
      content: this.inputTodo,
      completed: false
    })

    this.inputTodo = ""
    this.toggleAddFormOpen()
  }

  editTodo(id : number) {    
    const item = this.todos.find(item => item.id === id)

    item ? item.content = this.newInputTodo : null

    this.editIndex = -1
  }

  increaseHours() {
    let hours = this.addForm.get('hoursControl')!.value;
    hours = (hours + 1) % 24;
    this.addForm.get('hoursControl')!.setValue(hours);
  }

  decreaseHours() {
    let hours = this.addForm.get('hoursControl')!.value;
    hours = (hours - 1 + 24) % 24;
    this.addForm.get('hoursControl')!.setValue(hours);
  }

  increaseMinutes() {
    let minutes = this.addForm.get('minutesControl')!.value;
    minutes = (minutes + 15) % 60;
    this.addForm.get('minutesControl')!.setValue(minutes);
  }

  decreaseMinutes() {
    let minutes = this.addForm.get('minutesControl')!.value;
    minutes = (minutes - 15 + 60) % 60;
    this.addForm.get('minutesControl')!.setValue(minutes);
  }
}
