import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';

@Component({
  selector: 'app-time-control',
  template: `
    <div class="time-control">
      <button (click)="decreaseHours()">-</button>
      <input [formControl]="hoursControl" type="number" min="0" max="23">
      <button (click)="increaseHours()">+</button>
      :
      <button (click)="decreaseMinutes()">-</button>
      <input [formControl]="minutesControl" type="number" min="0" max="59">
      <button (click)="increaseMinutes()">+</button>
    </div>
  `,
  styleUrls: ['./time-control.component.css']
})
export class TimeControlComponent {
  hoursControl: FormControl;
  minutesControl: FormControl;

  constructor() {
    this.hoursControl = new FormControl(0);
    this.minutesControl = new FormControl(0);
  }

  increaseHours() {
    let hours = this.hoursControl.value;
    hours = (hours + 1) % 24;
    this.hoursControl.setValue(hours);
  }

  decreaseHours() {
    let hours = this.hoursControl.value;
    hours = (hours - 1 + 24) % 24;
    this.hoursControl.setValue(hours);
  }

  increaseMinutes() {
    let minutes = this.minutesControl.value;
    minutes = (minutes + 15) % 60;
    this.minutesControl.setValue(minutes);
  }

  decreaseMinutes() {
    let minutes = this.minutesControl.value;
    minutes = (minutes - 15 + 60) % 60;
    this.minutesControl.setValue(minutes);
  }
}
