import { NgModule, isDevMode } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { FormsModule } from '@angular/forms';

import { AppRoutingModule } from './app-routing.module';
import { AppComponent } from './app.component';
import { TitleComponent } from './components/title/title.component';
import { TodosComponent } from './components/todos/todos.component';
import { PlusIconComponent } from './components/icons/plus-icon/plus-icon.component';
import { DeleteIconComponent } from './components/icons/delete-icon/delete-icon.component';
import { EditIconComponent } from './components/icons/edit-icon/edit-icon.component';
import { ReactiveFormsModule } from '@angular/forms';
import { TimeControlComponent } from './components/form/time-control/time-control.component';
import { MinusIconComponent } from './components/icons/minus-icon/minus-icon.component';
import { AddFormComponent } from './components/form/add-form/add-form.component';
import { StoreModule } from '@ngrx/store';
import { StoreDevtoolsModule } from '@ngrx/store-devtools';


@NgModule({
  declarations: [
    AppComponent,
    TitleComponent,
    TodosComponent,
    PlusIconComponent,
    DeleteIconComponent,
    EditIconComponent,
    TimeControlComponent,
    MinusIconComponent,
    AddFormComponent
  ],
  imports: [
    BrowserModule,
    AppRoutingModule,
    FormsModule,
    ReactiveFormsModule,
    StoreModule.forRoot({}),
    StoreDevtoolsModule.instrument({
      maxAge: 25, // Retains last 25 states
      logOnly: !isDevMode(), // Restrict extension to log-only mode
      autoPause: true, // Pauses recording actions and state changes when the extension window is not open
      trace: false, //  If set to true, will include stack trace for every dispatched action, so you can see it in trace tab jumping directly to that part of code
      traceLimit: 75, // maximum stack trace frames to be stored (in case trace option was provided as true)
    }),
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
