import { Component, Input } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-time-control',
  template: `
    <div [formGroup]="parentForm">
      <div class="time-input">
        <div class="time-input-element">
            <div class="time-input-buttons">
                <button class="time-input-buttons" (click)="increaseHours()"><app-plus-icon/></button>
                <button class="time-input-buttons" (click)="decreaseHours()"><app-minus-icon/></button>
            </div>
        </div>
        <input class="time-input-control" formControlName="hoursControl"  type="number" min="0" max="23">
        :
        <div class="time-input-element">
            <input class="time-input-control" formControlName="minutesControl" type="number" min="0" max="59">        </div>
            <div class="time-input-buttons">
                <button class="time-input-buttons" (click)="increaseMinutes()"><app-plus-icon/></button>
                <button class="time-input-buttons" (click)="decreaseMinutes()"><app-minus-icon/></button>
            </div>
        </div>
    </div>
  `,
  styleUrls: ['./time-control.component.css']
})
export class TimeControlComponent {
  @Input() parentForm: FormGroup;

  constructor() {
    this.parentForm = new FormGroup({
      hours: new FormControl(),
      minutes: new FormControl()
    });
  }

  increaseHours() {
    let hours = parseInt(this.parentForm.get('hoursControl')!.value);
    hours = (hours + 1) % 24;
    this.parentForm.get('hoursControl')!.setValue(this.padZero(hours));
  }

  decreaseHours() {
    let hours = parseInt(this.parentForm.get('hoursControl')!.value);
    hours = (hours - 1 + 24) % 24;
    this.parentForm.get('hoursControl')!.setValue(this.padZero(hours));
  }

  increaseMinutes() {
    let minutes = parseInt(this.parentForm.get('minutesControl')!.value);
    minutes = (minutes + 15) % 60;
    this.parentForm.get('minutesControl')!.setValue(this.padZero(minutes));
  }

  decreaseMinutes() {
    let minutes = parseInt(this.parentForm.get('minutesControl')!.value);
    minutes = (minutes - 15 + 60) % 60;
    this.parentForm.get('minutesControl')!.setValue(this.padZero(minutes));
  }

  padZero(value: number): string {
    return value.toString().padStart(2, '0');
  }
}
