import { Component, Input } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-time-control',
  templateUrl: './time-control.component.html',
  styleUrls: ['./time-control.component.css']
})
export class TimeControlComponent {
  @Input() parentForm: FormGroup;

  constructor() {
    this.parentForm = new FormGroup({
      hours: new FormControl(),
      minutes: new FormControl()
    });
  }

  increaseHours() {
    let hours = parseInt(this.parentForm.get('hoursControl')!.value);
    hours = (hours + 1) % 24;
    this.parentForm.get('hoursControl')!.setValue(this.padZero(hours));
  }

  decreaseHours() {
    let hours = parseInt(this.parentForm.get('hoursControl')!.value);
    hours = (hours - 1 + 24) % 24;
    this.parentForm.get('hoursControl')!.setValue(this.padZero(hours));
  }

  increaseMinutes() {
    let minutes = parseInt(this.parentForm.get('minutesControl')!.value);
    minutes = (minutes + 15) % 60;
    this.parentForm.get('minutesControl')!.setValue(this.padZero(minutes));
  }

  decreaseMinutes() {
    let minutes = parseInt(this.parentForm.get('minutesControl')!.value);
    minutes = (minutes - 15 + 60) % 60;
    this.parentForm.get('minutesControl')!.setValue(this.padZero(minutes));
  }

  padZero(value: number): string {
    return value.toString().padStart(2, '0');
  }
}
