import { createAction, props } from "@ngrx/store";
import { Todo } from "src/app/models/Todo";
import { FormBuilder, FormGroup } from '@angular/forms';

// export const getTodoById = createAction(
//     '[Todo Page] Get Todo By Id',
//     props<{ id : number }>()
// );

export const addTodo = createAction(
    '[Todo Page] Add Todo',
    props<{ todo: Todo }>()
);

export const editTodo = createAction(
    '[Todo Page] Edit Todo',
    props<{ id: number, todo: Todo }>()
);


export const removeTodo = createAction(
    '[Todo Page] Remove Todo',
    props<{ id: number }>()
);

export const handleCompleted = createAction(
    '[Todo Page] Switch Todo Completed Property',
    props<{ id : number }>()
)

export const loadTodos = createAction('[Todo Page] Load Todos');

export const loadTodosSuccess = createAction(
    '[Todo API] Todo Load Success',
    props<{ todos: Todo[] }>()
);

export const loadTodosFailure = createAction(
    '[Todo API] Todo Load Failure',
    props<{ error: string }>()
);
