import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Todo } from 'src/app/models/Todo';
@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css']
})
export class TodosComponent implements OnInit {

  todos!: Todo[];

  addForm: FormGroup
  editForm: FormGroup

  newInputTodo:string = ""

  editIndex: number = -1;

  addFormOpen: boolean = false

  constructor() {
    this.addForm = new FormGroup({
      nameControl: new FormControl(),
      importanceControl: new FormControl(),
      hoursControl: new FormControl("12"),
      minutesControl: new FormControl("00")
    })

    this.editForm = new FormGroup({
      nameControl: new FormControl(this.todos.filter((element, i) => i !== this.editIndex)[0].content),
      importanceControl: new FormControl(this.todos.filter((element, i) => i !== this.editIndex)[0].importance),
      hoursControl:  new FormControl(this.todos.filter((element, i) => i !== this.editIndex)[0].deadline.split(':')[0]),
      minutesControl:  new FormControl(this.todos.filter((element, i) => i !== this.editIndex)[0].deadline.split(':')[1])
    })
  }

  ngOnInit(): void {
    this.todos = [
      {
        id: 0,
        content: 'First todo',
        importance: 'Medium',
        deadline: '12:00',
        completed: false
      },
      {
        id: 1,
        content: 'Second todo',
        importance: 'High',
        deadline: '12:00',
        completed: true
      },
    ]
  }

  toggleDone (id : number) : void {
    console.log("done")
    this.todos.map((element, i) => {
      if(i == id) element.completed = !element.completed

      return element
    })
    this.editIndex = -1

    console.log(this.todos)
  }

  toggleEdit  (id : number, event : any) : void {
    event.stopPropagation()
    if(this.editIndex == id) {
      this.editIndex = -1
    } else {
      this.editIndex = id
    }
  }

  toggleAddFormOpen () : void {
    this.addFormOpen = !this.addFormOpen
  }

  deleteTodo(id : number) {
    this.todos = this.todos.filter((element, i) => i !== id)
    this.editIndex = -1
  }

  addTodo () {

    console.log(this.addForm)
    if(this.addForm.get('nameControl')?.toString().trim() == "") return

    var newId : number = 0
    if(this.todos.length > 0) {
      newId = this.todos[this.todos.length - 1].id + 1
    }

    this.todos.push({
      id: newId,
      content: this.addForm.get('nameControl')!.value.toString(),
      importance: 'Medium',
      deadline: '12:00',
      completed: false
    })

    this.addForm.get('nameControl')!.setValue('')
    this.toggleAddFormOpen()
  }

  editTodo(id : number) {    
    const item = this.todos.find(item => item.id === id)

    item ? item.content = this.newInputTodo : null

    this.editIndex = -1
  }
}
