import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Todo } from 'src/app/models/Todo';

@Component({
  selector: 'app-edit-todo',
  templateUrl: './edit-todo.component.html',
  styleUrls: ['./edit-todo.component.css']
})
export class EditTodoComponent {

  @Input() idToEdit!: number
  @Input() editIndex!: number
  @Input() todos!: Todo[]
  @Input() inputPlaceholder!: string
  @Input() newInputTodo!: Todo

  @Output()
  toggleEdit: EventEmitter<object> = new EventEmitter<object>()


  onToggle(value: number, event: any) {
    this.toggleEdit.emit({value: value, event: event})
  }

  editTodo(id : number) {    
    let item = this.todos.find(item => item.id === id)

    item ? item = this.newInputTodo : null

    this.editIndex = -1
  }
}
<p>Edit todo:</p>
<form class="edit-tab-form" (submit)="editTodo(idToEdit)">
    <input 
        type="text"
        name="newInputTodo"
        class="edit-todo-input"
        placeholder="{{inputPlaceholder}}"
        [(ngModel)]="newInputTodo"
    />
    <div class="tail-buttons">
        <input type="submit" value="Edit" class="todo-edit-button edit" />
        <input type="button" (click)="onToggle(idToEdit, $event)" value="Cancel" class="todo-edit-button cancel" />
    </div>

</form>
