import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { EffectsModule } from '@ngrx/effects';
import { StoreModule } from '@ngrx/store';

import { TodosComponent } from './todos.component';
import { TodoService } from './todos.service';
import { TodoEffects } from 'src/app/state/todos/todo.effects';
import { todoReducer } from 'src/app/state/todos/todo.reducer';
import { TodoFormComponent } from '../form/todo-form/todo-form.component';
import { TimeControlComponent } from '../form/time-control/time-control.component';
import { DeleteIconComponent } from '../icons/delete-icon/delete-icon.component';
import { EditIconComponent } from '../icons/edit-icon/edit-icon.component';
import { MinusIconComponent } from '../icons/minus-icon/minus-icon.component';
import { PlusIconComponent } from '../icons/plus-icon/plus-icon.component';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { FormService } from '../form/form.service';

@NgModule({
  imports: [
    CommonModule,
    StoreModule.forFeature('todos', todoReducer),
    EffectsModule.forFeature([TodoEffects]),
    FormsModule,
    ReactiveFormsModule,
  ],
  providers: [TodoService, FormService],
  declarations: [
    TodosComponent,
    PlusIconComponent,
    DeleteIconComponent,
    EditIconComponent,
    TimeControlComponent,
    MinusIconComponent,
    TodoFormComponent],
  exports: [TodosComponent],
})
export class TodosModule {}
