import { Component, Input } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-time-control',
  template: `
    <div [formGroup]="parentForm">
      <div class="time-control">
        <button (click)="increaseHours()">+</button>
        <div class="time-input">
          <input formControlName="hours" type="number" min="0" max="23">
          <span>:</span>
          <input formControlName="minutes" type="number" min="0" max="59">
        </div>
        <button (click)="decreaseHours()">-</button>
      </div>
    </div>
  `,
  styleUrls: ['./time-control.component.css']
})
export class TimeControlComponent {
  @Input() parentForm: FormGroup;

  constructor() {
    this.parentForm = new FormGroup({
      hours: new FormControl(12),
      minutes: new FormControl(0)
    });
  }

  increaseHours() {
    let hours = this.parentForm.get('hours')!.value;
    hours = (hours + 1) % 24;
    this.parentForm.get('hours')!.setValue(hours);
  }

  decreaseHours() {
    let hours = this.parentForm.get('hours')!.value;
    hours = (hours - 1 + 24) % 24;
    this.parentForm.get('hours')!.setValue(hours);
  }
}
