import { Component, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { Store, select } from '@ngrx/store';
import { Observable } from 'rxjs';
import { Todo } from 'src/app/models/Todo';
import { AppState } from 'src/app/state/app.state';
import { addTodo, editTodo, handleCompleted, loadTodos, removeTodo } from 'src/app/state/todos/todo.actions';
import { todosSelector } from 'src/app/state/todos/todo.selectors';
import { FormService } from '../form/form.service';
@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css']
})
export class TodosComponent implements OnInit {
  todos$: Observable<Todo[]>;

  newId : number = 2

  todoForm: FormGroup

  editIndex: number = -1;

  addFormOpen: boolean = false
  editFormOpen: boolean = false

  constructor(private store: Store<AppState>, private formService: FormService) {
    this.todos$ = this.store.pipe(select(todosSelector))

    this.todoForm = this.formService.getDefaultFormGrop()
  }

  ngOnInit(): void { 
    this.store.dispatch(loadTodos());
  }

  toggleDone (id : number) : void {
    this.store.dispatch(handleCompleted({id : id}))
  }

  toggleEdit  (id : number, event : any) : void {
    event.stopPropagation()
    this.editFormOpen = true
    this.editIndex = id
  }

  toggleAddFormOpen () : void {
    this.addFormOpen = !this.addFormOpen
  }

  toggleEditFormOpen () : void {
    this.editFormOpen = !this.editFormOpen
  }

  deleteTodo(id : number) {
    this.store.dispatch(removeTodo({ id: id }));
  }

  addTodo () {
    this.toggleAddFormOpen()

    this.store.dispatch(addTodo({ todo: {
        id: this.newId,
        content: this.todoForm.get('nameControl')?.value,
        importance: this.todoForm.get('importanceControl')?.value,
        deadline:  this.todoForm.get('hoursControl')?.value + ":" + this.todoForm.get('minutesControl')?.value,
        completed: false
    }}))

    this.newId++

    this.resetTodoForm()
  }

  editTodo() {    
    this.store.dispatch(editTodo({
      id: this.editIndex,
      todo: {
        id: this.editIndex,
        content: this.todoForm.get('nameControl')?.value,
        importance: this.todoForm.get('importanceControl')?.value,
        deadline:  this.todoForm.get('hoursControl')?.value + ":" + this.todoForm.get('minutesControl')?.value,
        completed: false
      }
    }))

    this.resetTodoForm()

    this.toggleEditFormOpen()
  }

  resetTodoForm() {
    this.todoForm = this.formService.getDefaultFormGrop()
  }

}
