import { Component, Input } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-time-control',
  template: `
    <div [formGroup]="parentForm">
      <div class="time-input">
        <div class="time-input-element">
            <div class="time-input-buttons">
                <button (click)="increaseHours()"><app-plus-icon/></button>
                <button (click)="decreaseHours()"><app-minus-icon/></button>
            </div>
        </div>
        <input class="time-input-control" formControlName="hoursControl" [defaultValue]="12" type="number" min="0" max="23">
        :
        <div class="time-input-element">
            <input class="time-input-control" formControlName="minutesControl" value="00" type="number" min="0" max="59">        </div>
            <div class="time-input-buttons">
                <button (click)="increaseMinutes()"><app-plus-icon/></button>
                <button (click)="decreaseMinutes()"><app-minus-icon/></button>
            </div>
        </div>
    </div>
  `,
  styleUrls: ['./time-control.component.css']
})
export class TimeControlComponent {
  @Input() parentForm: FormGroup;

  constructor() {
    this.parentForm = new FormGroup({
      hours: new FormControl(12),
      minutes: new FormControl(0)
    });
  }

  increaseHours() {
    let hours = this.parentForm.get('hoursControl')!.value;
    hours = (hours + 1) % 24;
    this.parentForm.get('hoursControl')!.setValue(hours);
  }

  decreaseHours() {
    let hours = this.parentForm.get('hoursControl')!.value;
    hours = (hours - 1 + 24) % 24;
    this.parentForm.get('hoursControl')!.setValue(hours);
  }

  increaseMinutes() {
    let minutes = this.parentForm.get('minutesControl')!.value;
    minutes = (minutes + 15) % 60;
    this.parentForm.get('minutesControl')!.setValue(minutes);
  }

  decreaseMinutes() {
    let minutes = this.parentForm.get('minutesControl')!.value;
    minutes = (minutes - 15 + 60) % 60;
    this.parentForm.get('minutesControl')!.setValue(minutes);
  }
}
