import { createReducer, on } from '@ngrx/store';
import *  as Actions from './todo.actions';
import { Todo } from 'src/app/models/Todo';

export interface TodoState {
  todos: Todo[];
  error: string;
  isLoading: boolean,
}

export const initialState: TodoState = {
  todos: [],
  error: "",
  isLoading: false,
};

export const todoReducer = createReducer(
  // Supply the initial state
  initialState,

  // Add the new todo to the todos array
  on(Actions.addTodo, (state, { todo }) => ({
    ...state,
    todos: [...state.todos, todo],
    error: "",
    isLoading: false
  })),

  on(Actions.editTodo, (state, { id, todo }) => ({
    ...state, 
    todos: state.todos.map((item, index) => {
      if (index === id) {
        return todo; // Update the todo at the specified id
      }
      return item; // Keep the other todos unchanged
    }) 
   })),


  // Remove the todo from the todos array
  on(Actions.removeTodo, (state, { id }) => ({
    ...state,
    todos: state.todos.filter((todo) => todo.id !== id),
  })),

  // Switch completed property false -> true, true -> false
  on(Actions.handleCompleted, (state, { id }) => ({
    ...state,
    todos: state.todos.map((element) => {
        if (element.id === id) {
          return {
            ...element,
            completed: !element.completed,
          };
        }
        return element;
      })
  })),

  // Trigger loading the todos
  on(Actions.loadTodos, (state) => ({ ...state, isLoading: true })),


  // Handle successfully loaded todos
  on(Actions.loadTodosSuccess, (state, { todos }) => ({
    ...state,
    todos: todos,
    error: "",
    isLoading: false,
  })),

  
  // Handle todos load failure
  on(Actions.loadTodosFailure, (state, { error }) => ({
    ...state,
    error: error,
    isLoading: false,
  }))
);
